package ex_27042024Loops;

public class Lab119 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 1;
        while (i <= 10) {
            System.out.println(i);
            i = i + 3;
        }

        int i1 = 10;
        while(i1>=1){
            System.out.println(i1);
            i1--;
        }

	}

}
