package ex_04052024MultiDimensionArray;

public class Lab140 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n = 3;
        for (int i = 1; i <= n; i++) {
            for (int j = 1; j <= i; j++) {
                System.out.print("*");
            }
            
            
            System.out.println("");
        }

	}

}
