package PacticeQuestion;

public class Lab23SwappingtowInteger {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		swapTwoNumbers( 10, 20);

	}

	static void swapTwoNumbers(int a1, int b1) {
		// TODO Auto-generated method stub
		int a=b1;
		int b=b1-a1;
		System.out.println("Before Swapping the Values");
		System.out.println(a1);
		System.out.println(b1);
		System.out.println("After Swapping the Values");
		System.out.println(a);
		System.out.println(b);
		
		
		
	}

}
