package ex_18042024AllAboutOperators;

public class Lab028 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=27;
		int b= 10;
		
		System.out.println(a+b);  //+
		System.out.println(a-b);  //-
		System.out.println(a*b);  //*
		System.out.println(a/b);  //\
		System.out.println(a%b);  //%
		

	}

}
